<h3 class="marginTopMedium"><?php _e('Einstellungen','sf_simple_gig_listing'); ?></h3><?php$options = get_option('sf_simple_gig_listing_options');?><div class="sf_sfgl_settings">  <form method="post" action="">    <input type="hidden" name="update_settings" />    <?php wp_nonce_field( 'update_settings', 'sfsgl_nonce_check' ); ?>    <div class="field">      <label for="show_amount_gigs" >        <?php _e('MAXIMALE ANZAHL','sf_simple_gig_listing'); ?>      </label>      <div class="sf_desc">        <p><?php _e('Wähle, wieviele Auftritte angezeigt werden sollen. Wähle 0, wenn alle Auftritte angezeigt werden sollen.','sf_simple_gig_listing'); ?></p>      </div>      <input type="text" id="show_amount_gigs" name="show_amount_gigs" value="<?php echo $options['show_amount_gigs']; ?>" placeholder="0"/>    </div>    <div class="field">      <label for="sorting" >        <?php _e('SORTIERUNG','sf_simple_gig_listing'); ?>      </label>      <div class="sf_desc">        <p><?php _e('Wähle, ob die Auftritte aufsteigend, oder absteigend angezeigt werden sollen.','sf_simple_gig_listing'); ?></p>      </div>      <select id="sorting" name="sorting" class="">        <option value="descending" <?php selected( $options['sorting'], 'descending' ); ?> ><?php _e('Absteigend','sf_simple_gig_listing'); ?></option>        <option value="ascending" <?php selected( $options['sorting'], 'ascending' ); ?>><?php _e('Aufsteigend','sf_simple_gig_listing'); ?></option>      </select>    </div>    <div class="field">      <label for="display_past_gigs" >        <?php _e('ZEIGE VERGANGENE AUFTRITTE','sf_simple_gig_listing'); ?>      </label>      <div class="sf_desc">        <p><?php _e('Wähle, ob bereits vergangene Auftritte angezeigt werden sollen.','sf_simple_gig_listing'); ?></p>      </div>      <select id="display_past_gigs" name="display_past_gigs" class="">        <option value="enable_past" <?php selected( $options['display_past_gigs'], 'enable_past' ); ?> ><?php _e('Enable','sf_simple_gig_listing'); ?></option>        <option value="disable_past" <?php selected( $options['display_past_gigs'], 'disable_past' ); ?>><?php _e('Disable','sf_simple_gig_listing'); ?></option>      </select>    </div>    <div class="field">      <label for="css_style" >        <?php _e('CSS STYLE','sf_simple_gig_listing'); ?>      </label>      <div class="sf_desc">        <p><?php _e('Wähle deine bevorzugte Ansicht.','sf_simple_gig_listing'); ?></p>      </div>      <select id="css_style" name="css_style" class="">        <option value="simple" <?php selected( $options['css_style'], 'simple' ); ?> ><?php _e('Simple','sf_simple_gig_listing'); ?></option>        <option value="icon_wide" <?php selected( $options['css_style'], 'icon_wide' ); ?>><?php _e('Icons + Wide','sf_simple_gig_listing'); ?></option>      </select>    </div>    <p>    	<input type="submit" name="submit" id="submit" class="button button-primary" value="<?php _e('Änderungen speichern','sf_simple_gig_listing'); ?>"  />    </p>  </form></div>